import{_ as i,W as l,X as s,a0 as t}from"./framework-7a1bedf3.js";const e="/assets/1560049891419-cdeac056.png",o={},p=t('<h1 id="ubuntu安装" tabindex="-1"><a class="header-anchor" href="#ubuntu安装" aria-hidden="true">#</a> Ubuntu安装</h1><p>安装步骤：</p><ul><li><p>准备iso镜像文件：略</p></li><li><p>打开VMware workstation软件，选择【新建虚拟机】，然后【下一步】...</p></li><li><p>最后点击【完成】</p></li><li><p>配置：</p><ul><li><p>设置root（super user）密码命令：sudo passwd root</p></li><li><p>安装vim编辑器：sudo apt-get install vim</p></li><li><p>安装ssh服务：sudo apt-get install openssh-server</p></li><li><p>编辑sshd文件，通过终端工具连接（scrt）：sudo vim /etc/ssh/sshd_config</p><ul><li><p>将PermitRootLogin prohibit-pasdword 改为：PermitRootLogin yes</p><figure><img src="'+e+'" alt="1560049891419" tabindex="0" loading="lazy"><figcaption>1560049891419</figcaption></figure></li></ul></li><li><p>重启ssh服务：sudo /etc/init.d/ssh restart</p></li><li><p>关闭防火墙：sudo ufw disable</p></li></ul></li><li><p>查看Ubuntu系统版本：uname -a</p></li><li><p>解决Ubuntu16.04版本下网速慢的问题：</p><ul><li>sudo gedit /etc/modprobe.d/iwlwifi.conf</li><li>vim /etc/modprobe.d/iwlwifi.conf <ul><li>在配置文件的空白处添加：options iwlwifi 11n_disable=1</li></ul></li></ul></li></ul>',3),u=[p];function n(a,r){return l(),s("div",null,u)}const c=i(o,[["render",n],["__file","docker-linux-安装.html.vue"]]);export{c as default};
